import React from 'react';
import { connect } from 'react-redux';

import { userActions } from '../_actions';
import { UiInput }  from '../components/Ui'
import { UiButton }  from '../components/Ui'
import '../index.scss'


class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        // reset login status
        this.props.dispatch(userActions.logout());

        this.state = {
            username: '',
            password: '',
            submitted: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { username, password } = this.state;
        const { dispatch } = this.props;
        if (username && password) {
            dispatch(userActions.login(username, password));
        }
    }

    render() {
        const { loggingIn } = this.props;
        const { username, password, submitted } = this.state;
        return (
            <div className="home">
                <div className="col">
                    <form name="form" onSubmit={this.handleSubmit}>
                       <div className="home_input">
                            <UiInput type="text" name="username" placeholder="Логин" value={username} onChange={this.handleChange}/>
                            {submitted && !username &&
                                <div className="help-block">Необходим логин</div>
                            }
                        </div>
                        <div className="home_input">
                            <UiInput type="password" name="password" placeholder="Пароль" value={password} onChange={this.handleChange}/>
                            {submitted && !password &&
                                <div className="help-block">Необходим пароль</div>
                            }
                        </div>
                        <UiButton value="Войти"/>
                        <div>
                            <input type="checkbox" id="scales" name="scales"/>
                            <label forHtml="scales">Запомнить пароль</label>
                        </div>
                        <div>Забыли пароль?</div>
                        {loggingIn &&
                            <img src="data:image/gif;base64," />
                        }
                    </form>
                </div>
            </div>
        );
    }
}

function mapStateToProps(state) {
    const { loggingIn } = state.authentication;
    return {
        loggingIn
    };
}

const connectedLoginPage = connect(mapStateToProps)(LoginPage);
export { connectedLoginPage as LoginPage }; 